      for (int v : adj[u]) {
        if (!visited[v]) {
          can_move = true;
          dfs(v, prob / adj[u].size());
        }
      }
      if (!can_move) {
          expected_turns += prob * path.size();
      }
      visited[u] = false;
      path.pop_back();
    };
    dfs(start_node,1.0);
    cout << expected_turns << endl;
  }
  return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        double[] ans = new double[n];
        for (int i = 0; i < n; i++) {
            ans[i] = solve(i, graph);
        }
        for (int i = 0; i < n; i++) {
            System.out.println(ans[i]);
        }
    }

    static double solve(int start, List<List<Integer>> graph) {
        double ans = 0;
        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(start, new HashSet<>()));
        while (!q.isEmpty()) {
            Pair p = q.poll();
            int u = p.u;
            Set<Integer> visited = p.visited;
            ans += 1;
            List<Integer> neighbors = graph.get(u);
            int count = 0;
            for (int v : neighbors) {
                if (!visited.contains(v)) {
                    count++;
                }
            }
            if (count == 0) {
                continue;
            }
            for (int v : neighbors) {
                if (!visited.contains(v)) {
                    Set<Integer> newVisited = new HashSet<>(visited);
                    newVisited.add(u);
                    newVisited.add(v);
                    q.offer(new Pair(v, newVisited));
                }
            }
        }
        return ans;
    }

    static class Pair {
        int u;
        Set<Integer> visited;

        Pair(int u, Set<Integer> visited) {
            this.u = u;
            this.visited = visited;
        }
    }
}

