#define MOD 1000000007

int main() {
    int N, K, X;
    scanf("%d %d %d", &N, &K, &X);
    int S[K];
    for (int i = 0; i < K; i++) {
        scanf("%d", &S[i]);
    }
    long long dp[N + 1][X + 1];
    for (int i = 0; i <= N; i++) {
        for (int j = 0; j <= X; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j <= X; j++) {
            if (dp[i][j] == 0) continue;
            for (int k = i + 1; k <= N; k++) {
                if (j + k - i <= X) {
                    dp[k][j + k - i] = (dp[k][j + k - i] + dp[i][j]) % MOD;
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 0; i <= X; i++) {
        ans = (ans + dp[N][i]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}

