        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        int[] p = new int[n];
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
            a[i] = sc.nextInt();
        }
        List<List<Integer>> children = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            children.add(new ArrayList<>());
        }
        for (int i = 0; i < n; i++) {
            if (p[i] != -1) {
                children.get(p[i]).add(i);
            }
        }
        for (int i = 0; i < q; i++) {
            int type = sc.nextInt();
            if (type == 1) {
                int v = sc.nextInt();
                int d = sc.nextInt();
                long x = sc.nextInt();
                Queue<Integer> q1 = new LinkedList<>();
                q1.offer(v);
                Set<Integer> visited = new HashSet<>();
                visited.add(v);
                a[v] += x;
                int level = 0;
                while (!q1.isEmpty() && level < d) {
                    int size = q1.size();
                    for (int j = 0; j < size; j++) {
                        int curr = q1.poll();
                        for (int child : children.get(curr)) {
                            if (!visited.contains(child)) {
                                a[child] += x;
                                visited.add(child);
                                q1.offer(child);
                            }
                        }
                    }
                    level++;
                }
            } else if (type == 2) {
                int v = sc.nextInt();
                int d = sc.nextInt();
                long sum = a[v];
                Queue<Integer> q1 = new LinkedList<>();
                q1.offer(v);
                Set<Integer> visited = new HashSet<>();
                visited.add(v);
                int level = 0;
                while (!q1.isEmpty() && level < d) {
                    int size = q1.size();
                    for (int j = 0; j < size; j++) {
                        int curr = q1.poll();
                        for (int child : children.get(curr)) {
                            if (!visited.contains(child)) {
                                sum += a[child];
                                visited.add(child);
                                q1.offer(child);
                            }
                        }
                    }
                    level++;
                }
                System.out.println(sum);
            } else {
                int pr = sc.nextInt();
                long ar = sc.nextInt();
                p = Arrays.copyOf(p, n + 1);
                a = Arrays.copyOf(a, n + 1);
                p[n] = pr;
                a[n] = ar;
                children.add(new ArrayList<>());
                children.get(pr).add(n);
                n++;
            }
        }
        sc.close();
    }
}

