        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                String query = "";
                for (int k = 0; k < n; k++) {
                    if (k == i || k == j) {
                        query += "1";
                    } else {
                        query += "0";
                    }
                }
                out.println("? " + query);
                out.flush();
                int response = Integer.parseInt(br.readLine());
                if (response > 0) {
                    edges.add(new Pair(i, j));
                    return;
                }
            }
        }
    }

