import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        String t = sc.next();
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            int c = sc.nextInt();
            int d = sc.nextInt();
            String sSub = s.substring(a - 1, b);
            String tSub = t.substring(c - 1, d);
            if (solve(sSub, tSub)) {
                System.out.println("YES");
            } else {
                System.out.println("NO");
            }
        }
    }

    public static boolean solve(String s, String t) {
        if (s.length() == 0 && t.length() == 0) return true;
        if (s.length() == 0 || t.length() == 0) return false;
        Queue<String> q = new LinkedList<>();
        q.offer(s);
        Set<String> visited = new HashSet<>();
        visited.add(s);
        while (!q.isEmpty()) {
            String curr = q.poll();
            if (curr.equals(t)) return true;
            for (int i = 0; i < curr.length(); i++) {
                String next = replace(curr, i);
                if (!visited.contains(next)) {
                    q.offer(next);
                    visited.add(next);
                }
            }
            for (int i = 0; i < curr.length() - 2; i++) {
                String sub = curr.substring(i, i + 3);
                if (sub.equals("AAA") || sub.equals("BBB")) {
                    String next = curr.substring(0, i) + curr.substring(i + 3);
                    if (!visited.contains(next)) {
                        q.offer(next);
                        visited.add(next);
                    }
                }
            }
        }
        return false;
    }

    public static String replace(String s, int i) {
        char[] chars = s.toCharArray();
        if (chars[i] == 'A') {
            chars[i] = 'B';
            char[] newChars = new char[chars.length + 1];
            System.arraycopy(chars, 0, newChars, 0, i + 1);
            newChars[i + 1] = 'B';
            System.arraycopy(chars, i + 1, newChars, i + 2, chars.length - i - 1);
            return new String(newChars);
        } else {
            chars[i] = 'A';
            char[] newChars = new char[chars.length + 1];
            System.arraycopy(chars, 0, newChars, 0, i + 1);
            newChars[i + 1] = 'A';
            System.arraycopy(chars, i + 1, newChars, i + 2, chars.length - i - 1);
            return new String(newChars);
        }
    }
}

