        s = s.replace('AAA', '')
        s = s.replace('BBB', '')
        return s

    def can_transform(s, t):
        s_norm = normalize(s)
        t_norm = normalize(t)

        if len(s_norm) != len(t_norm):
            return False


        for i in range(len(s_norm)):
          if (s_norm[i] == 'A' and t_norm[i] == 'B') or (s_norm[i] == 'B' and t_norm[i] == 'A'):
