dp = {}
def count_sequences(current_sequence):
    if len(current_sequence) > n:
        return 1

    last_element = current_sequence[-1]

    state = tuple(current_sequence)
    if state in dp:
        return dp[state]

    count = 0
    for next_element in range(1, n + 1):
        next_sequence = current_sequence + [next_element]

        valid = True
        if len(next_sequence) > 1:

            last_len = next_sequence[-2]

            if len(next_sequence) <= last_len + 1 :
                if next_sequence[-1] != next_sequence[-2]:
                    valid = False

        if valid:
            count = (count + count_sequences(next_sequence)) % mod

    dp[state] = count
    return count

