#define MAXN 100005
#define MAXM 100005
#define MAXQ 100005

int n, m, q;
int a[MAXM], b[MAXM];
int color[MAXN];
int dist[MAXN];
int adj[MAXN][11];
int num_adj[MAXN];


void bfs(int start, int d) {
    for (int i = 1; i <= n; i++) {
        dist[i] = -1;
    }
    dist[start] = 0;
    int q[MAXN];
    int head = 0, tail = 1;
    q[head] = start;
    while (head < tail) {
        int u = q[head++];
        if (dist[u] > d) continue;
        for (int i = 0; i < num_adj[u]; i++) {
            int v = adj[u][i];
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q[tail++] = v;
            }
        }
    }
}

