    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a[i], &b[i]);
        adj[a[i]][num_adj[a[i]]++] = b[i];
        adj[b[i]][num_adj[b[i]]++] = a[i];
    }
    scanf("%d", &q);
    for (int i = 0; i < q; i++) {
        int v, d, c;
        scanf("%d %d %d", &v, &d, &c);
        bfs(v, d);
        for (int j = 1; j <= n; j++) {
            if (dist[j] != -1 && dist[j] <= d) {
                color[j] = c;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        printf("%d\n", color[i]);
    }
    return 0;
}

