public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long x = sc.nextLong();
        long y = sc.nextLong();
        HashMap<Integer, ArrayList<Long>> colors = new HashMap<>();
        for (int i = 0; i < n; i++) {
            int c = sc.nextInt();
            long w = sc.nextLong();
            if (!colors.containsKey(c)) {
                colors.put(c, new ArrayList<>());
            }
            colors.get(c).add(w);
        }
        long[] fact = new long[n + 1];
        fact[0] = 1;
        for (int i = 1; i <= n; i++) {
            fact[i] = (fact[i - 1] * i) % 1000000007;
        }
        long ans = 1;
        long[] cnt = new long[n + 1];
        for (ArrayList<Long> list : colors.values()) {
            cnt[list.size()]++;
        }
        for (int i = 1; i <= n; i++) {
            long num = cnt[i];
            if (num == 0) continue;
            long cur = fact[i];
            long curAns = 1;
            for (int j = 0; j < num; j++) {
                curAns = (curAns * cur) % 1000000007;
                cur = (cur * i) % 1000000007;
            }
            ans = (ans * curAns) % 1000000007;
        }

        long total = 0;
        for (int i = 1; i <= n; i++) {
            total += cnt[i] * i;
        }
        long modInverse = power(fact[(int) total], 1000000007 - 2, 1000000007);
        ans = (ans * modInverse) % 1000000007;
        System.out.println(ans);
    }

    public static long power(long base, long exp, long mod) {
        long res = 1;
        base %= mod;
        while (exp > 0) {
            if ((exp & 1) == 1) res = (res * base) % mod;
            base = (base * base) % mod;
            exp >>= 1;
        }
        return res;
    }
}

