import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[2 * n - 1];
        for (int i = 0; i < 2 * n - 1; i++) {
            a[i] = sc.nextInt();
        }
        Arrays.sort(a);
        Set<List<Integer>> set = new HashSet<>();
        do {
            List<Integer> b = new ArrayList<>();
            for (int i = 1; i <= n; i++) {
                int[] sub = Arrays.copyOfRange(a, 0, 2 * i - 1);
                Arrays.sort(sub);
                b.add(sub[(2 * i - 1) / 2]);
            }
            set.add(b);
        } while (nextPermutation(a));
        System.out.println(set.size());
    }

    static boolean nextPermutation(int[] a) {
        int n = a.length;
        int i = n - 2;
        while (i >= 0 && a[i] >= a[i + 1]) {
            i--;
        }
        if (i == -1) {
            return false;
        }
        int j = n - 1;
        while (a[j] <= a[i]) {
            j--;
        }
        swap(a, i, j);
        reverse(a, i + 1, n - 1);
        return true;
    }

    static void swap(int[] a, int i, int j) {
        int temp = a[i];
        a[i] = a[j];
        a[j] = temp;
    }

    static void reverse(int[] a, int i, int j) {
        while (i < j) {
            swap(a, i, j);
            i++;
            j--;
        }
    }
}

