            boolean possible = true;
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    boolean expected = (i >> k) % 2 == 1;
                    if (((i >> j) % 2 == 1) != honest[j][k] && expected != honest[j][k]) {
                        possible = false;
                        break;
                    }
                }
                if (!possible) break;
            }
            if (possible) {
                for (int j = 0; j < N; j++) {
                    result[j] = (i >> j) % 2;
                }
                break;
            }
        }
        bw.write("!");
        for (int i = 0; i < N; i++) {
            bw.write(result[i]);
        }
        bw.write("\n");
