#define MAXN 100005
#define MAXM 200005

int n, m;
int parent[MAXN * MAXN];
int adj[MAXN][MAXN];

int find(int x) {
    if (parent[x] == x) return x;
    return parent[x] = find(parent[x]);
}

void unite(int x, int y) {
    x = find(x);
    y = find(y);
    if (x != y) parent[x] = y;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        adj[u][v] = adj[v][u] = 1;
    }
    for (int i = 0; i < n * n; i++) parent[i] = i;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 1; l <= n; l++) {
                    if (adj[i][k] && adj[j][l]) {
                        unite((i - 1) * n + j - 1, (k - 1) * n + l - 1);
                    }
                }
            }
        }
    }
    int count = 0;
    for (int i = 0; i < n * n; i++) {
        if (parent[i] == i) count++;
    }
    printf("%d\n", count);
    return 0;
}

