        do {
            boolean ok = true;
            int[] cur = new int[n];
            for (int i = 0; i < n; i++) {
                cur[i] = x[p.get(i)];
            }
            int[] fin = new int[n];
            Arrays.fill(fin, 0);
            int cnt = 0;
            List<Integer> used = new ArrayList<>();

            while(cnt < n){
                int best_idx = -1;
                int min_val = Integer.MAX_VALUE;
                for(int i = 0; i < n; i++){
                    if(fin[i] == 0 && cur[i] < min_val){
                        min_val = cur[i];
                        best_idx = i;
                    }
                }
                if(best_idx == -1) break;

                boolean moved = false;
                if(cur[best_idx] -1 > 0){
                    boolean possible = true;
                    for(int j = 0; j < n; j++){
                        if(j != best_idx && cur[j] == cur[best_idx]-1){
                            possible = false;
                            break;
                        }
                    }
                    if(possible){
                        cur[best_idx]--;
                        moved = true;
                    }
                }
                if(!moved && cur[best_idx] -2 > 0){
                    boolean possible = true;
                    for(int j = 0; j < n; j++){
                        if(j != best_idx && cur[j] == cur[best_idx]-2){
                            possible = false;
                            break;
                        }
                    }
                    if(possible){
                        cur[best_idx] -= 2;
                        moved = true;
                    }
                }
                if(cur[best_idx] <= 0){
                    fin[best_idx] = 1;
                    cnt++;
                }
            }

            if(cnt == n) ans = (ans + 1) % mod;
        } while (nextPermutation(p));
        System.out.println(ans);
    }

    public static boolean nextPermutation(List<Integer> arr) {
        int i = arr.size() - 2;
        while (i >= 0 && arr.get(i) >= arr.get(i + 1)) {
            i--;
        }
        if (i == -1) {
            return false;
        }
        int j = arr.size() - 1;
        while (arr.get(j) <= arr.get(i)) {
            j--;
        }
        Collections.swap(arr, i, j);
        Collections.reverse(arr.subList(i + 1, arr.size()));
        return true;
    }
}

