import java.util.*;
import java.io.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        char[][] grid = new char[n][n];
        for (int i = 0; i < n; i++) {
            String line = br.readLine();
            for (int j = 0; j < n; j++) {
                grid[i][j] = line.charAt(j);
            }
        }

        int ans = solve(grid);
        System.out.println(ans);
    }

    static int solve(char[][] grid) {
        int n = grid.length;
        int count = 0;
        for (int i = 0; i < n; i++) {
            if (grid[0][i] == '.') {
                count++;
            }
        }

        if (count == 0) return 0;

        Queue<char[][]> q = new LinkedList<>();
        q.offer(grid);
        Set<String> visited = new HashSet<>();
        visited.add(Arrays.deepToString(grid));

        int steps = 0;
        while (!q.isEmpty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                char[][] curr = q.poll();
                boolean allBlack = true;
                for (int r = 0; r < n; r++) {
                    for (int c = 0; c < n; c++) {
                        if (curr[r][c] == '.') {
                            allBlack = false;
                            break;
                        }
                    }
                    if (!allBlack) break;
                }
                if (allBlack) return steps;

                for (int r = 0; r < n; r++) {
                    char[] row = curr[r].clone();
                    for (int c = 0; c < n; c++) {
                        char[][] next = copyGrid(curr);
                        for (int k = 0; k < n; k++) {
                            next[k][c] = row[k];
                        }
                        String nextStr = Arrays.deepToString(next);
                        if (!visited.contains(nextStr)) {
                            visited.add(nextStr);
                            q.offer(next);
                        }
                    }
                }
            }
            steps++;
        }
        return -1;
    }


    static char[][] copyGrid(char[][] grid) {
        int n = grid.length;
        char[][] copy = new char[n][n];
        for (int i = 0; i < n; i++) {
            System.arraycopy(grid[i], 0, copy[i], 0, n);
        }
        return copy;
    }
}

