char* process(char* t) {
    int n = strlen(t);
    int i;
    while (1) {
        i = -1;
        for (int j = 0; j < n - 1; j++) {
            if (t[j] == t[j + 1]) {
                i = j;
                break;
            }
        }
        if (i == -1) break;
        if (t[i] == 'z') {
            for (int j = i; j < n - 2; j++) {
                t[j] = t[j + 2];
            }
            n -= 2;
            t = (char*)realloc(t, n + 1);
            t[n] = '\0';
        } else {
            t[i] = t[i] + 1;
            for (int j = i + 1; j < n - 1; j++) {
                t[j] = t[j + 1];
            }
            n--;
            t = (char*)realloc(t, n + 1);
            t[n] = '\0';
        }
    }
    return t;
}

