        adj = [[] for _ in range(n)]
        cur = bin(i)[2:].zfill(n - 1)
        for j in range(n - 1):
            u, v = edges[j]
            if cur[j] == '0':
                adj[u].append(v)
            else:
                adj[v].append(u)

        d_sum = 0
        for s in range(n):
            for t in range(n):
                if s == t:
                    continue
                q = [(s, 0)]
                visited = {s}
                min_dist = float('inf')
                while q:
                    curr, dist = q.pop(0)
                    if curr == t:
                        min_dist = dist
                        break
                    for neighbor in adj[curr]:
                        if neighbor not in visited:
                            visited.add(neighbor)
                            q.append((neighbor, dist))


                q = [(s, 0)]
                visited = {s}
                dist_s_t = float('inf')
                while q:
                    curr, dist = q.pop(0)
                    if curr == t:
                        dist_s_t = dist
                        break
                    for neighbor in adj[curr]:
                        if neighbor not in visited:
                            visited.add(neighbor)
                            q.append((neighbor, dist + 1 if neighbor not in adj[curr] else dist))

                d_sum += dist_s_t

        if d_sum < min_d:
