        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long[] ans = new long[n];
        PriorityQueue<long[]> pq = new PriorityQueue<>(Comparator.comparingLong(x -> -x[1]));
        for (int i = 0; i < n; i++) {
            pq.offer(new long[]{i, a[i]});
        }
        while (!pq.isEmpty()) {
            long[] cur = pq.poll();
            int idx = (int) cur[0];
            long val = cur[1];
            long max = -1;
            int maxIdx = -1;
            for (long[] item : pq) {
                if (item[1] > max) {
                    max = item[1];
                    maxIdx = (int) item[0];
                }
            }
            if (maxIdx == -1 || max < val) {
                ans[idx] += val;
            } else {
                long diff = val - max;
                ans[idx] += diff + 1;
                pq.offer(new long[]{idx, max});

            }

        }
        for (long x : ans) {
            System.out.println(x);
        }
    }
}

