    std::vector<long long> a(n);
    for (int i = 0; i < n; ++i) std::cin >> a[i];
    std::sort(a.begin(), a.end());
    std::vector<long long> b = a;
    std::vector<long long> result;
    do {
        std::vector<long long> current = b;
        bool swapped = true;
        while (swapped) {
            swapped = false;
            for (int i = 0; i < n - 1; ++i) {
                if (gcd(current[i], current[i + 1]) == 1) {
                    std::swap(current[i], current[i + 1]);
                    swapped = true;
                }
            }
        }
        if (result.empty() || current > result) {
            result = current;
        }
    } while (std::next_permutation(b.begin(), b.end()));
    for (int i = 0; i < n; ++i) std::cout << result[i] << (i == n - 1 ? "" : " ");
    std::cout << std::endl;
    return 0;
}

