  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  if (n == 1) {
    cout << 0 << endl;
    return 0;
  }

  vector<vector<int>> adj(n);
  for (const auto& edge : edges) {
    adj[get<0>(edge)].push_back(get<1>(edge));
    adj[get<1>(edge)].push_back(get<0>(edge));
  }

  vector<int> degree(n, 0);
  for (int i = 0; i < n; ++i) {
    degree[i] = adj[i].size();
  }

  int max_degree = 0;
  for (int d : degree) {
    max_degree = max(max_degree, d);
  }

