    for (long long i = 2; i <= n; i++) {
        long long temp = i;
        for (long long j = 2; j * j <= temp; j++) {
            if (temp % j == 0) {
                int c = 0;
                while (temp % j == 0) {
                    temp /= j;
                    c++;
                }
                int k=0;
                for(int l=0; l<count; ++l){
                    if(prime[l] == j) k=1;
                }
                if(k==0){
                    prime[count++] = j;
                }
            }
        }
        if (temp > 1) {
            int k=0;
            for(int l=0; l<count; ++l){
                if(prime[l] == temp) k=1;
            }
            if(k==0){
                prime[count++] = temp;
            }
        }
