        bool found = false;
        for (long long k = 0; k < primes.size(); ++k) {
          if (primes[k] == j) {
            counts[j]++;
            found = true;
            break;
          }
        }
        if (!found) {
          primes.push_back(j);
          counts[j]++;
        }
        num /= j;
      }
    }
    if (num > 1) {
      bool found = false;
      for (long long k = 0; k < primes.size(); ++k) {
        if (primes[k] == num) {
          counts[num]++;
