#include <stdio.h>

#define MOD 1000000007

long long dp[1001][1001];
long long comb[1001][1001];

int main() {
    int n, a, b, c, d;
    scanf("%d %d %d %d %d", &n, &a, &b, &c, &d);

    for (int i = 0; i <= n; i++) {
        comb[i][0] = 1;
        for (int j = 1; j <= i; j++) {
            comb[i][j] = (comb[i - 1][j - 1] + comb[i - 1][j]) % MOD;
        }
    }

    dp[0][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= i; j++) {
            for (int k = a; k <= b && k <= i; k++) {
                for (int l = c; l <= d && l * k <= i; l++) {
                    if (i >= k * l) {
                        dp[i][j + l] = (dp[i][j + l] + dp[i - k * l][j] * comb[i -1][k -1] % MOD * comb[i - k][k * l - 1] % MOD) % MOD;

                    }
                }
            }
        }
    }

    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[n][i]) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}

#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, a, b, c, d;
  cin >> n >> a >> b >> c >> d;

  long long mod = 1e9 + 7;
  long long count = 0;

  function<void(vector<int>, int)> solve = [&](vector<int> groups, int remaining) {
    if (remaining == 0) {
      vector<long long> freq(n + 1, 0);
      for (int size : groups) {
        freq[size]++;
      }

      bool ok = true;
      for (int i = 1; i <= n; ++i) {
        if (freq[i] > 0 && !(freq[i] >= c && freq[i] <= d)) {
          ok = false;
          break;
        }
      }
      if (ok) count = (count + 1) % mod;
      return;
    }

    for (int i = a; i <= min(b, remaining); ++i) {
      vector<int> next_groups = groups;
      next_groups.push_back(i);
      solve(next_groups, remaining - i);
    }
  };

