        }
    }
    dp[1][0] = 0;
    for (int i = 1; i <= n; i++) {
        for (int mask = 0; mask < (1 << m); mask++) {
            if (dp[i][mask] == -1e18) continue;
            for (int next_mask = 0; next_mask < (1 << m); next_mask++) {
                if ((mask | next_mask) == mask) continue;
                long long cur_deliciousness = 0;
                for (int j = 0; j < m; j++) {
