public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        int M = sc.nextInt();
        long[] A = new long[N - 1];
        for (int i = 0; i < N - 1; i++) {
            A[i] = sc.nextLong();
        }
        long[][] B = new long[N][M];
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < M; j++) {
                B[i][j] = sc.nextLong();
            }
        }
        long maxHappiness = Long.MIN_VALUE;
        for (int i = 0; i < (1 << M); i++) {
            long currentHappiness = 0;
            long currentDistance = 0;
            int currentRestaurant = 0;
            List<Integer> usedTickets = new ArrayList<>();
            for (int j = 0; j < M; j++) {
                if ((i >> j) % 2 == 1) {
                    usedTickets.add(j);
                }
            }
            if (usedTickets.isEmpty()) continue;

            long[][] dp = new long[N][1 << M];
            for (int k = 0; k < N; k++) {
                Arrays.fill(dp[k], Long.MIN_VALUE);
            }
            dp[0][0] = 0;
            for (int k = 0; k < N; k++) {
                for (int mask = 0; mask < (1 << M); mask++) {
                    if (dp[k][mask] == Long.MIN_VALUE) continue;
                    for (int nextRestaurant = 0; nextRestaurant < N; nextRestaurant++) {
                        long nextMask = mask;
                        long nextHappiness = dp[k][mask];
                        long nextDistance = currentDistance;
                        if (k != nextRestaurant) {
                            long dist = 0;
                            for (int l = Math.min(k, nextRestaurant); l < Math.max(k, nextRestaurant); l++) {
                                dist += A[l];
                            }
                            nextDistance += dist;
                        }
                        for (int ticketIndex : usedTickets) {
                            if ((mask >> ticketIndex) % 2 == 0) {
                                nextHappiness += B[nextRestaurant][ticketIndex];
                                nextMask |= (1 << ticketIndex);
                            }
                        }
                        dp[nextRestaurant][nextMask] = Math.max(dp[nextRestaurant][nextMask], nextHappiness - nextDistance);
                    }
                }
            }
            for(int k=0; k<N; ++k) maxHappiness = Math.max(maxHappiness, dp[k][(1<<M)-1]);
        }
        System.out.println(maxHappiness);
    }
}

