#define INF INT_MAX

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int edges[m][3];
    for (int i = 0; i < m; i++) {
        scanf("%d %d %d", &edges[i][0], &edges[i][1], &edges[i][2]);
        edges[i][0]--; 
        edges[i][1]--; 
    }

    int dist[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dist[i][j] = (i == j) ? 0 : INF;
        }
    }

    for (int i = 0; i < m; i++) {
        dist[edges[i][0]][edges[i][1]] = dist[edges[i][1]][edges[i][0]] = edges[i][2];
    }

    for (int k = 0; k < n; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (dist[i][k] != INF && dist[k][j] != INF && dist[i][k] + dist[k][j] < dist[i][j]) {
                    dist[i][j] = dist[i][k] + dist[k][j];
                }
            }
        }
    }

    int count = 0;
    for (int i = 0; i < m; i++) {
        int u = edges[i][0];
        int v = edges[i][1];
        int w = edges[i][2];
        int shortest = dist[u][v];
        if (shortest != w)
            count++;
        else{
            int flag =0;
            for(int j=0; j<n; ++j){
                for(int k=0; k<n; ++k){
                    if(j==k) continue;
                    if(dist[j][k] == dist[j][u] + w + dist[v][k] && dist[j][u] != INF && dist[v][k] != INF){
                        flag = 1;
                        break;
                    }
                     if(dist[j][k] == dist[j][v] + w + dist[u][k] && dist[j][v] != INF && dist[u][k] != INF){
                        flag = 1;
                        break;
                    }
                }
                if(flag) break;
            }
            if(!flag) count++;
        }
    }

    printf("%d\n", count);

    return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<Edge> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int w = sc.nextInt();
            edges.add(new Edge(u, v, w));
        }
        sc.close();

        int count = 0;
        for (int i = 0; i < m; i++) {
            boolean used = false;
            for (int j = 1; j <= n; j++) {
                for (int k = 1; k <= n; k++) {
                    if (j == k) continue;
                    int[][] dist = dijkstra(n, edges, i);
                    if (dist[j][k] != Integer.MAX_VALUE) {
                        int[][] dist2 = dijkstra(n, edges, -1);
                        if (dist[j][k] == dist2[j][k]) {

                            boolean edge_used = false;
                            List<Integer> path = getPath(dist, j,k);
                            for(int a = 0; a < path.size() -1; a++){
                                if((edges.get(i).u == path.get(a) && edges.get(i).v == path.get(a+1)) || (edges.get(i).v == path.get(a) && edges.get(i).u == path.get(a+1)))
                                    edge_used = true;
                            }

                            if(edge_used){
                                used = true;
                                break;
                            }

                        }
                    }
                }
                if (used) break;
            }
            if (!used) count++;
        }
        System.out.println(count);
    }


    static int[][] dijkstra(int n, List<Edge> edges, int remove) {
        int[][] dist = new int[n + 1][n + 1];
        for (int i = 1; i <= n; i++) {
            Arrays.fill(dist[i], Integer.MAX_VALUE);
            dist[i][i] = 0;
        }
        List<Edge> cur_edges = new ArrayList<>(edges);
        if(remove != -1) cur_edges.remove(remove);
        for (Edge edge : cur_edges) {
            dist[edge.u][edge.v] = Math.min(dist[edge.u][edge.v], edge.w);
            dist[edge.v][edge.u] = Math.min(dist[edge.v][edge.u], edge.w);
        }
        for (int k = 1; k <= n; k++) {
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (dist[i][k] != Integer.MAX_VALUE && dist[k][j] != Integer.MAX_VALUE) {
                        dist[i][j] = Math.min(dist[i][j], dist[i][k] + dist[k][j]);
                    }
                }
            }
        }
        return dist;
    }
    static List<Integer> getPath(int[][] dist, int start, int end){
        List<Integer> path = new ArrayList<>();
        path.add(start);
        int cur = start;
        while(cur != end){
            for(int i = 1; i < dist.length; i++){
                if(dist[cur][i] != Integer.MAX_VALUE && dist[cur][i] + dist[i][end] == dist[cur][end]){
                    path.add(i);
                    cur = i;
                    break;
                }
            }
        }
        path.add(end);
        return path;
    }

    static class Edge {
        int u, v, w;

        Edge(int u, int v, int w) {
            this.u = u;
            this.v = v;
            this.w = w;
        }
    }
}

