          if (all_in_range) {
            bool can_paint = true;
            for (int m = l; m <=j; ++m){
              if(colored[m]){
                can_paint = false;
                break;
              }
            }
            if(can_paint){
              for (int m = l; m <= j; ++m) {
                colored[m] = true;
                current_a[m] = (current_a[m] > 0 ? current_a[m] : 0);
              }
              painted = true;
              break;
            }
          }
        }
        if (!painted) {
          possible = false;
          break;
        }
      }
    }
    if (possible) {
      for (int j = 0; j < n; ++j) {
        if ((i >> j) & 1) {
          current_score += current_a[j];
        }
      }
      max_score = max(max_score, current_score);
    }
  }
  cout << max_score << endl;
  return 0;
}

