void function(int n, int a[], int p[], int used[], int k, int *count) {
    if (k == n) {
        int flag = 1;
        for (int i = 0; i < n; i++) {
            if (p[i] != a[i] && p[p[i]-1] != a[i]) {
                flag = 0;
                break;
            }
        }
        if (flag) {
            (*count)++;
        }
        return;
    }
    for (int i = 1; i <= n; i++) {
        if (used[i] == 0) {
            used[i] = 1;
            p[k] = i;
            function(n, a, p, used, k + 1, count);
            used[i] = 0;
        }
    }
}

