char s[MAXN];
int adj[MAXN][2];
int dist[MAXN];
int visited[MAXN];
int colors[MAXN];

void bfs(int start, int d) {
    memset(dist, -1, sizeof(dist));
    memset(visited, 0, sizeof(visited));
    int q[MAXN], head = 0, tail = 0;
    q[tail++] = start;
    dist[start] = 0;
    visited[start] = 1;
    while (head < tail) {
        int u = q[head++];
        if (dist[u] <= d) {
            for (int i = 0; i < adj[u][0]; ++i) {
                int v = adj[u][i + 1];
                if (!visited[v]) {
                    visited[v] = 1;
                    dist[v] = dist[u] + 1;
                    q[tail++] = v;
                }
            }
        }
    }
}

