                bfs(i, d);
                for (int j = 1; j <= n; ++j) {
                    if (visited[j]) colors[j] = 1;
                }
                int hash = 0;
                for (int j = 1; j <= n; ++j) {
                    hash = hash * 2 + colors[j];
                }
                count = count | (1 << hash);
            }
        }
    }
    printf("%d\n", __builtin_popcount(count));
