public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        String s = sc.next();
        Set<String> colors = new HashSet<>();
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '1') {
                for (int d = 0; d < n; d++) {
                    String color = "";
                    Queue<Pair> q = new LinkedList<>();
                    q.offer(new Pair(i, 0));
                    Set<Integer> visited = new HashSet<>();
                    while (!q.isEmpty()) {
                        Pair p = q.poll();
                        int u = p.first;
                        int dist = p.second;
                        if (dist <= d && !visited.contains(u)) {
                            color += "1";
                            visited.add(u);
                            for (int v : adj.get(u)) {
                                q.offer(new Pair(v, dist + 1));
                            }
                        } else if (!visited.contains(u)) {
                            color += "0";
                        }
                    }
                    colors.add(color);
                }
            }
        }
        System.out.println(colors.size());
        sc.close();
    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }
}

