int find_set_road(int v) {
    if (v == parent_road[v])
        return v;
    return parent_road[v] = find_set_road(parent_road[v]);
}

int find_set_rail(int v) {
    if (v == parent_rail[v])
        return v;
    return parent_rail[v] = find_set_rail(parent_rail[v]);
}

void union_sets_road(int a, int b) {
    a = find_set_road(a);
    b = find_set_road(b);
    if (a != b) {
        if (size_road[a] < size_road[b])
            swap(a, b);
        parent_road[b] = a;
        size_road[a] += size_road[b];
    }
}

void union_sets_rail(int a, int b) {
    a = find_set_rail(a);
    b = find_set_rail(b);
    if (a != b) {
        if (size_rail[a] < size_rail[b])
            swap(a, b);
        parent_rail[b] = a;
        size_rail[a] += size_rail[b];
    }
}

