def solve():
    n, k, l = map(int, sys.stdin.readline().split())
    roads = []
    for _ in range(k):
        roads.append(list(map(int, sys.stdin.readline().split())))
    railways = []
    for _ in range(l):
        railways.append(list(map(int, sys.stdin.readline().split())))

    def bfs(edges, n):
        adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        connected_components = []
        visited = [False] * (n + 1)

        def dfs(u, component):
            visited[u] = True
            component.append(u)
            for v in adj[u]:
                if not visited[v]:
                    dfs(v, component)

        for i in range(1, n + 1):
            if not visited[i]:
                component = []
                dfs(i, component)
                connected_components.append(component)
        return connected_components

    road_components = bfs(roads, n)
    railway_components = bfs(railways, n)

    road_connected = [0] * (n + 1)
    for i in range(len(road_components)):
        for node in road_components[i]:
            road_connected[node] = i

    railway_connected = [0] * (n + 1)
    for i in range(len(railway_components)):
        for node in railway_components[i]:
            railway_connected[node] = i

    ans = []
    for i in range(1, n + 1):
        count = 0
        for j in range(1, n + 1):
            if road_connected[i] == road_connected[j] and railway_connected[i] == railway_connected[j]:
                count += 1
        ans.append(count)
    print(*ans)

