using namespace std;

int get_root(vector<int>& parent, int i) {
  if (parent[i] == i) return i;
  return parent[i] = get_root(parent, parent[i]);
}

void unite_sets(vector<int>& parent, vector<int>& rank, int a, int b) {
  a = get_root(parent, a);
  b = get_root(parent, b);
  if (a != b) {
    if (rank[a] < rank[b]) swap(a, b);
    parent[b] = a;
    if (rank[a] == rank[b]) rank[a]++;
  }
}

