    railways = []
    for _ in range(l):
        railways.append(list(map(int, sys.stdin.readline().split())))

    def bfs(adj, start_node):
        visited = set()
        queue = [start_node]
        visited.add(start_node)
        while queue:
            curr = queue.pop(0)
            for neighbor in adj.get(curr, []):
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append(neighbor)
        return visited

    road_adj = {}
    for u, v in roads:
        road_adj.setdefault(u, []).append(v)
        road_adj.setdefault(v, []).append(u)

    railway_adj = {}
    for u, v in railways:
        railway_adj.setdefault(u, []).append(v)
        railway_adj.setdefault(v, []).append(u)

    ans = []
    for i in range(1, n + 1):
        road_connected = bfs(road_adj, i)
        railway_connected = bfs(railway_adj, i)
        count = 0
        for j in range(1, n + 1):
            if j in road_connected and j in railway_connected:
                count += 1
        ans.append(count)
    print(*ans)

