        count++;
        long long dist = manhattan_distance(current.first, current.second);
        for (int i = 0; i < n; ++i) {
            if (!visited[i]) {
                if (manhattan_distance(current.first, i) == dist) {
                    q.push_back({current.first, i});
                    visited[i] = true;
                } else if (manhattan_distance(current.second, i) == dist) {
                    q.push_back({current.second, i});
                    visited[i] = true;
                }
            }
        }
    }
    cout << count << endl;
