import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt() - 1;
        int b = sc.nextInt() - 1;
        long[] x = new long[n];
        long[] y = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            y[i] = sc.nextLong();
        }
        long dist = Math.abs(x[a] - x[b]) + Math.abs(y[a] - y[b]);
        Set<Pair> visited = new HashSet<>();
        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(a, b));
        visited.add(new Pair(a, b));
        while (!q.isEmpty()) {
            Pair p = q.poll();
            int i = p.first;
            int j = p.second;
            for (int k = 0; k < n; k++) {
                if (Math.abs(x[i] - x[k]) + Math.abs(y[i] - y[k]) == dist) {
                    Pair next = new Pair(i, k);
                    if (!visited.contains(next)) {
                        visited.add(next);
                        q.offer(next);
                    }
                }
            }
        }
        System.out.println(visited.size());
    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = Math.min(first, second);
            this.second = Math.max(first, second);
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Pair pair = (Pair) o;
            return first == pair.first && second == pair.second;
        }

        @Override
        public int hashCode() {
            return Objects.hash(first, second);
        }
    }
}

