    if (n % 2 == 1) {
        long long ans = power(k, n / 2 + 1, mod);
        printf("%lld\n", ans);
    } else {
        long long ans = (power(k, n / 2, mod) * (k + 1)) % mod;
        long long g = power(2, mod - 2, mod);
        ans = (ans * g) % mod;
        printf("%lld\n", ans);
    }
    return 0;
}

