public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int c = sc.nextInt();

        long[][] dp = new long[n + 1][n + 1];
        dp[0][0] = 1;

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (dp[i][j] == 0) continue;

                if (i + 1 <= n) {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD;
                }
                if (i + 2 <= n && j + 1 <= n) {
                    dp[i + 2][j + 1] = (dp[i + 2][j + 1] + dp[i][j]) % MOD;
                }
                if (i + 3 <= n && j + 2 <= n) {
                    dp[i + 3][j + 2] = (dp[i + 3][j + 2] + dp[i][j]) % MOD;
                }
            }
        }

        long ans = 0;
        if (dp[n][a] > 0) {
            ans = dp[n][a];
            long[][] dp2 = new long[b+1][c+1];
            dp2[0][0] = 1;
            for (int i=0; i<=b; ++i){
                for (int j=0; j<=c; ++j){
                    if (dp2[i][j] == 0) continue;
                    if (i+1 <= b) dp2[i+1][j] = (dp2[i+1][j] + dp2[i][j]) % MOD;
                    if (j+1 <= c) dp2[i][j+1] = (dp2[i][j+1] + dp2[i][j]) % MOD;

                }
            }
            ans = (ans * dp2[b][c]) % MOD;
        }

        System.out.println(ans);
    }
}

MOD = 10**9 + 7

def solve():
    n, a, b, c = map(int, input().split())

    dp = {}

    def count_ways(rem_n, rem_a, rem_b, rem_c, used):
        if rem_n == 0:
            return 1

        state = (rem_n, rem_a, rem_b, rem_c, used)
        if state in dp:
            return dp[state]

        count = 0

        if rem_a > 0 and not (used & (1 << (rem_n))):
            count = (count + count_ways(rem_n - 1, rem_a - 1, rem_b, rem_c, used | (1 << rem_n) | (1 << (rem_n + 1)))) % MOD

        if rem_b > 0 and not (used & (1 << rem_n)) and not (used & (1 << (rem_n+1))):
            count = (count + count_ways(rem_n - 1, rem_a, rem_b - 1, rem_c, used | (1 << rem_n) | (1 << (rem_n + 2)))) % MOD

        if rem_c > 0 and not (used & (1 << rem_n)) and not (used & (1 << (rem_n+1))) and not (used & (1 << (rem_n+2))):
            count = (count + count_ways(rem_n - 1, rem_a, rem_b, rem_c - 1, used | (1 << rem_n) | (1 << (rem_n + 3)))) % MOD

        dp[state] = count
        return count

    print(count_ways(n, a, b, c, 0))

