    scanf("%d %d", &N, &Q);
    int A[Q], B[Q];
    for (int i = 0; i < Q; i++) {
        scanf("%d %d", &A[i], &B[i]);
    }
    int min_pos = 1, max_pos = 1;
    int ball_pos = 1;
    for (int i = 0; i < Q; i++) {
        if (ball_pos == A[i]) {
            ball_pos = B[i];
        } else if (ball_pos == B[i]) {
            ball_pos = A[i];
        }
        min_pos = (ball_pos < min_pos) ? ball_pos : min_pos;
        max_pos = (ball_pos > max_pos) ? ball_pos : max_pos;

    }
    int ans = max_pos - min_pos + 1;
    printf("%d\n", ans);
    return 0;
}

#include <iostream>
#include <vector>
#include <set>

using namespace std;

int main() {
  int n, q;
  cin >> n >> q;

  vector<pair<int, int>> ops(q);
  for (int i = 0; i < q; ++i) {
    cin >> ops[i].first >> ops[i].second;
  }

  set<int> possible_positions;
  possible_positions.insert(1);

  for (int i = 0; i < (1 << (q + 1)); ++i) {
    set<int> current_possible_positions;
    current_possible_positions.insert(1);
    int ball_pos = 1;
    bool magic_used = false;

    for (int j = 0; j <= q; ++j) {
      if ((i >> j) & 1 && !magic_used) {
        if (ball_pos > 1) ball_pos--;
        else ball_pos++;
        magic_used = true;
      }

      if (j < q) {
        int a = ops[j].first;
        int b = ops[j].second;
        if (ball_pos == a) ball_pos = b;
        else if (ball_pos == b) ball_pos = a;
      }
    }
    current_possible_positions.insert(ball_pos);

    if (possible_positions.empty()){
        possible_positions = current_possible_positions;
    } else {
        set<int> intersection;
        for(int pos : possible_positions){
            if(current_possible_positions.count(pos)){
                intersection.insert(pos);
            }
        }
        possible_positions = intersection;
    }
  }

  cout << possible_positions.size() << endl;
  return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        int[] ab = new int[2 * q];
        for (int i = 0; i < 2 * q; i++) {
            ab[i] = sc.nextInt();
        }
        Set<Integer> set = new HashSet<>();
        set.add(1);
        for (int i = 0; i < q; i++) {
            int a = ab[2 * i];
            int b = ab[2 * i + 1];
            Set<Integer> nextSet = new HashSet<>();
            for (int x : set) {
                if (x == a) {
                    nextSet.add(b);
                } else if (x == b) {
                    nextSet.add(a);
                } else {
                    nextSet.add(x);
                }
            }
            set = nextSet;
        }
        int min = Integer.MAX_VALUE;
        int max = Integer.MIN_VALUE;
        for (int x : set) {
            min = Math.min(min, x);
            max = Math.max(max, x);
        }
        System.out.println(max - min + 1);
    }
