int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  int max_m = 0;
  for (int i = 0; i < (1 << n); ++i) {
    vector<int> subset;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        subset.push_back(j);
      }
    }

    if (subset.empty()) continue;

    bool ok = true;
    for (size_t j = 0; j < subset.size() - 1; ++j) {
      vector<int> path;
      vector<bool> visited(n, false);
      vector<int> q;
      q.push_back(subset[j]);
      visited[subset[j]] = true;
      int head = 0;
      bool found = false;
      while (head < q.size()) {
        int u = q[head++];
        if (u == subset[j + 1]) {
          found = true;
          break;
        }
        for (const auto& e : edges) {
          int v = -1;
          if (get<0>(e) == u && !visited[get<1>(e)]) {
            v = get<1>(e);
          } else if (get<1>(e) == u && !visited[get<0>(e)]) {
            v = get<0>(e);
          }
          if (v != -1) {
            q.push_back(v);
            visited[v] = true;
          }
        }
      }
      if (!found) {
        ok = false;
        break;
      }
      for (int k = 0; k < path.size(); k++) {
          bool in_subset = false;
          for (int l=0; l < subset.size(); ++l){
              if (path[k] == subset[l]) in_subset = true;
          }
          if (in_subset && path[k] != subset[j] && path[k] != subset[j+1]) ok = false;
      }


    }
    if (ok) {
      max_m = max(max_m, (int)subset.size());
    }
  }
  cout << max_m << endl;
  return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int max_m = 0;
        for (int i = 0; i < (1 << n); i++) {
            List<Integer> subset = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    subset.add(j);
                }
            }
            if (subset.size() <= max_m) continue;
            boolean ok = true;
            if (subset.size() > 1) {
                for (int j = 0; j < subset.size() - 1; j++) {
                    int u = subset.get(j);
                    int v = subset.get(j + 1);
                    Queue<Integer> q = new LinkedList<>();
                    q.add(u);
                    Set<Integer> visited = new HashSet<>();
                    visited.add(u);
                    List<Integer> path = new ArrayList<>();
                    path.add(u);
                    boolean found = false;
                    while (!q.isEmpty()) {
                        int curr = q.poll();
                        if (curr == v) {
                            found = true;
                            break;
                        }
                        for (int next : adj.get(curr)) {
                            if (!visited.contains(next)) {
                                visited.add(next);
                                q.add(next);
                                path.add(next);
                            }
                        }
                    }
                    if (!found) {
                        ok = false;
                        break;
                    }
                    for (int k = 0; k < path.size(); k++) {
                        int node = path.get(k);
                        if (node != u && node != v && subset.contains(node)) {
                            ok = false;
                            break;
                        }
                    }
                    if (!ok) break;
                }
            }
            if (ok) {
                max_m = Math.max(max_m, subset.size());
            }
        }
        System.out.println(max_m);
    }
}

