    for (int i = 0; i < (1 << (n - 1)); ++i) {
        int numCommas = 0;
        for (int j = 0; j < n - 1; ++j) {
            if ((i >> j) & 1) {
                numCommas++;
            }
        }
        if (numCommas > k) continue;

        long long maxNum = 0;
        int start = 0;
        for (int j = 0; j < n - 1; ++j) {
