    static List<Integer> solve(int n) {
        List<Integer> res = new ArrayList<>();
        int i = 1;
        int sum = 0;
        while (sum < n) {
            sum += i;
            res.add(i);
            i++;
        }
        if (sum == n) return res;
        int diff = sum - n;
        int j = res.size() -1;
        while(diff > 0){
            int val = res.get(j);
            if(val > diff){
                j--;
                continue;
            }
            res.remove(j);
            diff -= val;

        }
        return res;
    }
}

