    vector<int> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i];
        counts[x[i]]++;
    }

    int pairs = 0;
    for (auto const& [key, val] : counts) {
        pairs += val / 2;
        counts[key] %= 2;
    }

    vector<int> remainders;
    for (auto const& [key, val] : counts) {
        if (val > 0) {
            remainders.push_back(key % m);
        }
    }

    map<int, int> remainder_counts;
    for (int r : remainders) {
        remainder_counts[r]++;
    }

    for (auto const& [key, val] : remainder_counts) {
        if (key == 0) {
            pairs += val / 2;
            continue;
        }
        int complement = (m - key) % m;
        if (remainder_counts.count(complement)) {
            int num_pairs = min(val, remainder_counts[complement]);
            pairs += num_pairs;
            remainder_counts[key] -= num_pairs;
            remainder_counts[complement] -= num_pairs;
        }
    }

    cout << pairs << endl;

    return 0;
