long long solve() {
    long long n, a;
    scanf("%lld %lld", &n, &a);
    if (n == 1) return 1;
    long long l = 1, r = 2 * n;
    long long ans = 2 * n;
    while (l <= r) {
        long long mid = l + (r - l) / 2;
        long long cur = 1;
        long long time = 0;
        long long cookies = 0;
        while (cookies < n) {
            long long next_time = mid - time;
            if (cur == 1) {
                long long add = next_time;
                cookies += add;
                time += add;
            } else {
                long long add = next_time / cur * cur;
                cookies += add;
                time += add/cur;

            }
            if (cookies >= n) break;
            long long eat_time = a;
            if (time + eat_time > mid) break;
            time += eat_time;
            cur = cookies;

        }

        if (cookies >= n) {
            ans = mid;
            r = mid - 1;
        } else {
            l = mid + 1;
        }
    }
    return ans;
}

