    time = 1;
    cur = 1;

    while (cur < n) {
      long long next_eat = time;
      long long next_time = next_eat + a;
      long long add = (next_time - time) * rate;
      cur += add;
      if (cur >= n) break;
      time = next_time;
      rate = cur;
    }

    if (cur >= n) {
      ans = min(ans, time);
      right = mid - 1;
    } else {
      left = mid + 1;
    }
  }
  cout << ans << endl;
  return 0;
