import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        List<long[]> edges = new ArrayList<>();
        for (int i = 0; i < q; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            long c = sc.nextLong();
            for (int j = 0; j < n; j++) {
                edges.add(new long[]{(long) a, (long) b, c});
                a = (a + 1) % n;
                b = (b + 1) % n;
                c++;
            }
        }
        Collections.sort(edges, (e1, e2) -> Long.compare(e1[2], e2[2]));
        UnionFind uf = new UnionFind(n);
        long totalWeight = 0;
        for (long[] edge : edges) {
            long u = edge[0];
            long v = edge[1];
            long w = edge[2];
            if (!uf.isSame(u, v)) {
                uf.unite(u, v);
                totalWeight += w;
            }
        }
        System.out.println(totalWeight);
        sc.close();
    }

    static class UnionFind {
        int[] parent;
        int[] rank;

        UnionFind(int n) {
            parent = new int[n];
            rank = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
            }
        }

        int find(int x) {
            if (parent[x] == x) {
                return x;
            }
            return parent[x] = find(parent[x]);
        }

        boolean isSame(int x, int y) {
            return find(x) == find(y);
        }

        void unite(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);
            if (rootX != rootY) {
                if (rank[rootX] < rank[rootY]) {
                    parent[rootX] = rootY;
                } else {
                    parent[rootY] = rootX;
                    if (rank[rootX] == rank[rootY]) {
                        rank[rootX]++;
                    }
                }
            }
        }
    }
}

import sys

def solve():
    n, q = map(int, sys.stdin.readline().split())
    edges = []
    for _ in range(q):
        a, b, c = map(int, sys.stdin.readline().split())
        i = 0
        while True:
            na = (a + i) % n
            nb = (b + i) % n
            edges.append((na, nb, c + i))
            i += 1
            if i > 1000:
                break

    parent = list(range(n))
    rank = [0] * n

    def find(i):
        if parent[i] == i:
            return i
        parent[i] = find(parent[i])
        return parent[i]

    def union(i, j):
        root_i = find(i)
        root_j = find(j)
        if root_i != root_j:
            if rank[root_i] < rank[root_j]:
                parent[root_i] = root_j
            elif rank[root_i] > rank[root_j]:
                parent[root_j] = root_i
            else:
                parent[root_j] = root_i
                rank[root_i] += 1
            return True
        return False

    edges.sort(key=lambda x: x[2])
    total_weight = 0
    for u, v, w in edges:
        if union(u, v):
            total_weight += w

    print(total_weight)

