        if (sum + i <= n) {
            solution.push_back(i);
            sum += i;
        }
    }
    if (sum != n) {
        int diff = n - sum;
        for (int i = 1; i <= n; ++i) {
          if (diff == i) {
            solution.push_back(i);
            break;
          }
        }

