    for (int i = 0; i < N; i++) {
        can_communicate[i] = (bool *)malloc(N * sizeof(bool));
        for (int j = 0; j < N; j++) {
            can_communicate[i][j] = false;
        }
    }

    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            bool can_comm = false;
            for (int k = 0; k < K[i]; k++) {
                for (int l = 0; l < K[j]; l++) {
                    if (languages[i][k] == languages[j][l]) {
                        can_comm = true;
                        break;
                    }
                }
                if (can_comm) break;
            }
            can_communicate[i][j] = can_communicate[j][i] = can_comm;
        }
    }

    for (int k = 0; k < N; k++) {
        for (int i = 0; i < N; i++) {
            for (int j = i + 1; j < N; j++) {
                if (!can_communicate[i][j]) {
                    bool comm_through_k = false;
                    if (can_communicate[i][k] && can_communicate[k][j]) comm_through_k = true;
                    if (comm_through_k) can_communicate[i][j] = can_communicate[j][i] = true;
                }
            }
        }
    }


    bool all_communicate = true;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            if (!can_communicate[i][j]) {
                all_communicate = false;
                break;
            }
        }
        if (!all_communicate) break;
    }

