      vector<vector<bool>> adj_matrix(n, vector<bool>(n, false));
      int current_node = 0;
      for (int i = 0; i < m; ++i) {
        adj_matrix[current_node][c[i] -1] = true;
        current_node = c[i] - 1;
      }

      vector<vector<bool>> reachable(n, vector<bool>(n, false));
      for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
          if (adj_matrix[i][j]) reachable[i][j] = true;
        }
      }

      for (int k = 0; k < n; ++k) {
